import React, { useState, useEffect } from 'react';
import axios from 'axios'; // Para fazer a requisição à API

const AllSurveys = () => {
  const [surveys, setSurveys] = useState([]);
  const [expandedSurveyId, setExpandedSurveyId] = useState(null); // Controla qual entrevista está expandida
  const [expandedDetailsId, setExpandedDetailsId] = useState(null); // Controla a expansão dos detalhes do entrevistador/entrevistado

  // Função para carregar as entrevistas
  useEffect(() => {
    // Requisição para obter todas as entrevistas
    axios.get('http://localhost:4000/api/surveys')
      .then(response => {
        console.log('resposta: ', response.data);
        setSurveys(response.data);
      })
      .catch(error => {
        console.error('Erro ao obter as entrevistas:', error);
      });
  }, []);

  // Função para alternar a expansão da entrevista
  const toggleSurveyExpansion = (id) => {
    setExpandedSurveyId(expandedSurveyId === id ? null : id);
  };

  // Função para alternar a expansão dos detalhes do entrevistador/entrevistado
  const toggleDetailsExpansion = () => {
    setExpandedDetailsId(expandedDetailsId === null ? true : null);
  };

  // Função para baixar o arquivo
  const downloadFile = (fileName) => {
    const fileUrl = `http://localhost:4000/uploads/${fileName}`; // Exemplo, altere conforme seu caminho de upload
    const link = document.createElement('a');
    link.href = fileUrl;
    link.download = fileName;
    link.click();
  };

  return (
    <div>
      <h1>Entrevistas Realizadas</h1>

      {/* Exibindo as entrevistas */}
      {surveys.map((survey) => (
        <div key={survey.id_entrevista} className="survey">
          {/* Título da entrevista */}
          <div
            className="survey-header"
            onClick={() => toggleSurveyExpansion(survey.id_entrevista)}
            style={{ cursor: 'pointer', padding: '10px', backgroundColor: '#f0f0f0', marginBottom: '5px' }}
          >
            <h2>
              {survey.entrevistadorName} entrevistando {survey.entrevistadoName}
            </h2>
          </div>

          {/* Se a entrevista estiver expandida, mostramos as questões */}
          {expandedSurveyId === survey.id_entrevista && (
            <div className="survey-details" style={{ paddingLeft: '20px', display: 'flex', justifyContent: 'center' }}>
              {/* Detalhes do entrevistador e entrevistado */}
              <div style={{ flex: 1, padding: '10px', borderRight: '2px solid #ddd' }}>
                <h3>Entrevistador</h3>
                <ul>
                  <li><strong>Nome:</strong> {survey.entrevistadorName}</li>
                  <li><strong>Cargo:</strong> {survey.entrevistadorJobTitle}</li>
                  <li><strong>Localização:</strong> {survey.entrevistadorLocation}</li>
                  <li><strong>Área Funcional:</strong> {survey.entrevistadorFunctional_area}</li>
                </ul>
              </div>

              <div style={{ flex: 1, padding: '10px' }}>
                <h3>Entrevistado</h3>
                <ul>
                  <li><strong>Nome:</strong> {survey.entrevistadoName}</li>
                  <li><strong>Cargo:</strong> {survey.entrevistadoJobTitle}</li>
                  <li><strong>Localização:</strong> {survey.entrevistadoLocation}</li>
                  <li><strong>Área Funcional:</strong> {survey.entrevistadoFunctional_area}</li>
                </ul>
              </div>
            </div>
          )}

          {/* Perguntas e Respostas */}
          {expandedSurveyId === survey.id_entrevista && (
            <div className="questions-list" style={{ paddingLeft: '20px' }}>
              {survey.surveyDetails.map((question, index) => (
                <div key={index} className="question" style={{ marginBottom: '20px' }}>
                  <h3>{question.Pergunta}</h3>
                  <p><strong>Resposta:</strong> {question.Resposta}</p>
                  <p><strong>Comentários:</strong> {question.Comentarios || 'Nenhum comentário'}</p>
                  <p><strong>Normas Aplicáveis:</strong> {question.Normas_aplicaveis}</p>
                  <p><strong>Índice da Pergunta:</strong> {question.Indice_Pergunta}</p>
                  <p><strong>Âmbito:</strong> {question.Ambito}</p>

                  {/* Exibindo os arquivos de documentação apenas se houver */}
                  {question.Documentacao && question.Documentacao.trim() !== '' ? (
                    <div className="documentos">
                      <h4>Documentos:</h4>
                      <ul>
                        {question.Documentacao.split(',').map((fileName, idx) => (
                          <li key={idx}>
                            <a
                              href="#"
                              onClick={() => downloadFile(fileName.trim())}
                              style={{ color: 'blue', textDecoration: 'underline' }}
                            >
                              {fileName.trim()}
                            </a>
                          </li>
                        ))}
                      </ul>
                    </div>
                  ) : null}
                </div>
              ))}
            </div>
          )}
        </div>
      ))}
    </div>
  );
};

export default AllSurveys;
